import React from "react";
import "./Counter.scss";

const Counter = ((props) => {
    const {imagesArr} = props;
    const [counter, setCounter] = useState(0);

    const handleIncrement = () => {
        if (counter === imagesArr.length - 1) {
            setCounter(0);
        } else {
            setCounter(counter + 1);
        }
    };

    const handleDecrement = () => {
        if (counter === 0) {
          setCounter(imagesArr.length - 1);
        } else {
          setCounter(counter - 1);
        }
      };

      console.log(imagesArr);
    return (
    <div className="counterBox">
        <p onClick={handleDecrement} className="carousel__arrow carousel__arrow--left" />
        <p onClick={handleIncrement} className="carousel__arrow carousel__arrow--right" />
    </div>
    )
});

export default Counter;
