    <div className="carousel">
        <img
        src={leftArrow}
        alt="left arrow"
        onClick={handleDecrement}
        className="carousel__arrow carousel__arrow--left" />
        <img
        src={imagesArr[counter]} className="carousel__image" />
        <img
        src={rightArrow}
